import React, { Component } from "react";

class Message extends Component {
  state = {
    message: "",
    animating: false
  };

  componentDidUpdate({score, topScore}, prevState) {
    const newS = { animating: true };
    if (score === 0 && topScore === 0) {
      newS.message = "";
    } else if (score === 0 && topScore > 0) {
      newS.message = "wrong";
    } else {
      newS.message = "right";
    }
    if (score !== this.props.score || this.state.message !== newS.message) {
      this.setState(newS);
    }
  }

  renderMessage = () => {
    switch (this.state.message) {
    case "right":
      return "You guessed right";
    case "wrong":
      return "You guessed wrong";
    default:
      return "Click an image to start";
    }
  };

  render() {
    return (
      <li className={this.state.animating ? this.state.message : ""}
        imagestate={() => this.setState({ animating: false })}>
        {this.renderMessage()}
      </li>
    );
  }
}

export default Message;
